"**Itinerary**  (:point_right: shows primary phone):\n"

"|Name|Phone|Address|Details|\n"
"| --- | --- | --- | --- |\n"
LOOP &{Itinerary}
{
    IF &{Itinerary.IsRestaurant} THEN  {
        "|**Restaurant** " &{IRestaurant.Name} " " &{IRestaurant.Emoji}
        "||"  &{IRestaurant.Address} "|"  &{IRestaurant.ThisDriverOrders} " orders //"

        IF &{IRestaurant.IsCleanup} THEN  {
                IF &{IRestaurant.NoPics} THEN
                {  ":no_mobile_phones:**No pics**" }

               IF NOT &{IRestaurant.NoPics} THEN
               {  "camera:**Take pics**"  }
        }
        IF NOT &{IRestaurant.IsCleanup} THEN
        {  ":no_mobile_phones:**No pics**" }

        " " &{IRestaurant.Details}
       "|\n"
    }

    IF &{Itinerary.IsDelivery} THEN {
        "|" &{IConsumer.Name}
        "|" " :point_right:" &{IConsumer.CompactPhone}
         IF &{IConsumer.IsAltPhone} THEN { ", " &{IConsumer.CompactAltPhone}  }
        "|" &{IConsumer.Address} ", " &{IConsumer.City}  
        "|" IF &{IConsumer.IsCondo} THEN { ":warning:**Condo**" }
        " " &{IConsumer.RestaurantEmoji} " " &{IConsumer.Details}
        "|\n"
    }
}
