rows:
[header]

LOOP &{Driver}
{
    "@" &{Driver.UserName} ", your run starts at "
    &{Driver.FirstRestaurantName}  " at  "
    &{Driver.FirstRestaurantStartTime}  "."
    IF &{Driver.IsFirstRestaurantClosingBefore7PM} THEN
    {
        " This restaurant closes at "  &{Driver.FirstRestaurantClosingTime}  "."
    }
    "\n"
}

[SplitRestaurants]

IF ${AnySplitRestaurants} THEN
{
    "\n**Split Restaurants**\n\n"
}

LOOP &{SplitRestaurant}
{
    "|" &{SplitRestaurant.Name} " " &{SplitRestaurant.Emoji} " drivers"
    "|orders (" &{SplitRestaurant.TotalOrders} " total )|\n"
    "|---|---|\n"

    LOOP &{SplitRestaurant.Driver}
    {
        "|" &{Driver.UserName} "|" &{Driver.SplitRestaurantOrders} "|\n"
    }   
    "\n"
}
