     &{Consumer.StandardMeal} " std meal; "
  }
  IF &{Consumer.AlternateMeal}  THEN {
     &{Consumer.AlternateMeal} " "
     &{Consumer.AlternateMealType} " meal; "
  }
  IF &{Consumer.StandardGrocery} THEN {
    &{Consumer.StandardGrocery} " std grocery; "
  }
